mapper.select:
select
	id,
	code,
	name,
	status,
	icon_url,
	rate_type,
	rate,
	datasource_code,
	sort,
	create_time
from 
	wp_product_category



		and (
			 code like CONCAT(CONCAT('%', #{keywords }),'%')
			or name like CONCAT(CONCAT('%', #{keywords }),'%')
		)


		and code = #{code}


		and name = #{name}


		and status = #{status}


		and rate_type = #{rate_type}


		and datasource_code = #{datasource_code}


		and create_time >= #{create_time_begin}


		and create_time <= #{create_time_end}



	order by #{orderBy } #{direction }


	order by sort asc

select id, code, name, status, icon_url, rate_type, rate, datasource_code, sort, create_time from wp_product_category
and id = #{id}
and code = #{code}
and status = #{status}
limit 1

mapper.update:
update wp_product_category set
code=#{code, jdbcType=VARCHAR}, name=#{name, jdbcType=VARCHAR}, status=#{status, jdbcType=INTEGER}, icon_url=#{icon_url, jdbcType=VARCHAR}, rate_type=#{rate_type, jdbcType=INTEGER}, rate=#{rate, jdbcType=NUMERIC}, datasource_code=#{datasource_code, jdbcType=VARCHAR}, sort=#{sort, jdbcType=INTEGER},
where id = #{id}

mapper.select:
select 
	(case when count(0)>0 then 1 else 0 end) 
from wp_product_category

	and id<>#{id, jdbcType=INTEGER}
	and code=#{code, jdbcType=VARCHAR}
	and name=#{name, jdbcType=VARCHAR}
