mapper.select:
select
	id,
	cust_no,
	mobile,
	balance,
	frozen_money,
	nick_name,
	wx_openid,
	head_url,
	email,
	sex,
	birthday,
	is_borker,
	broker_id,
	h_number,
	h_level,
	operation_member_id,
	member_id,
	agent_member_id,
	pid,
	status,
	active_status,
	recharge_status,
	withdraw_status,
	login_status,
	trade_status,
	register_time,
	register_ip,
	last_login_time,
	last_login_ip,
	org_mobile,
	org_wx_openid,
	share_code
from 
	wp_customer



		and (
			 cust_no like CONCAT(CONCAT('%', #{keywords }),'%')
			or nick_name like CONCAT(CONCAT('%', #{keywords }),'%')
			or register_ip like CONCAT(CONCAT('%', #{keywords }),'%')
			or org_mobile like CONCAT(CONCAT('%', #{keywords }),'%')
			or share_code like CONCAT(CONCAT('%', #{keywords }),'%')
		)


		and cust_no = #{cust_no}


		and mobile = #{mobile}


		and nick_name = #{nick_name}


		and is_borker = #{is_borker}


		and operation_member_id = #{operation_member_id}


		and member_id = #{member_id}


		and agent_member_id = #{agent_member_id}


		and status = #{status}


		and active_status = #{active_status}


		and recharge_status = #{recharge_status}


		and withdraw_status = #{withdraw_status}


		and login_status = #{login_status}


		and trade_status = #{trade_status}


		and register_time >= #{register_time_begin}


		and register_time <= #{register_time_end}


		and register_ip = #{register_ip}


		and last_login_time >= #{last_login_time_begin}


		and last_login_time <= #{last_login_time_end}


		and org_mobile = #{org_mobile}


		and share_code = #{share_code}


		and h_number like CONCAT(#{h_number }, '%') and h_level > #{h_level }



	order by ${orderBy } ${sort_direction }


	order by id desc

select id, cust_no, mobile, password, balance, frozen_money, nick_name, wx_openid, head_url, email, sex, birthday, is_borker, broker_id, h_number, h_level, operation_member_id, member_id, agent_member_id, pid, status, active_status, recharge_status, withdraw_status, login_status, trade_status, register_time, register_ip, last_login_time, last_login_ip, org_mobile, org_wx_openid, share_code from wp_customer
and id = #{id}
and cust_no = #{cust_no}
and mobile = #{mobile}
and is_borker = #{is_borker}
and status = #{status}
and share_code = #{share_code}

mapper.update:
update wp_customer set
cust_no=#{cust_no, jdbcType=VARCHAR}, password=#{password, jdbcType=VARCHAR}, mobile=#{mobile, jdbcType=VARCHAR}, nick_name=#{nick_name, jdbcType=VARCHAR}, wx_openid=#{wx_openid, jdbcType=VARCHAR}, head_url=#{head_url, jdbcType=VARCHAR}, email=#{email, jdbcType=VARCHAR}, sex=#{sex, jdbcType=INTEGER}, birthday=#{birthday, jdbcType=TIMESTAMP}, status=#{status, jdbcType=INTEGER}, active_status=#{active_status, jdbcType=INTEGER}, recharge_status=#{recharge_status, jdbcType=INTEGER}, withdraw_status=#{withdraw_status, jdbcType=INTEGER}, login_status=#{login_status, jdbcType=INTEGER}, trade_status=#{trade_status, jdbcType=INTEGER}, last_login_time=#{last_login_time, jdbcType=TIMESTAMP}, last_login_ip=#{last_login_ip, jdbcType=VARCHAR}, org_mobile=#{org_mobile, jdbcType=VARCHAR}, org_wx_openid=#{org_wx_openid, jdbcType=VARCHAR}, share_code=#{share_code, jdbcType=VARCHAR},
where id = #{id}

update
	wp_customer
set 
	balance = balance + #{amount, jdbcType=NUMERIC }
where 
	id = #{id} 

		and balance >= -#{amount, jdbcType=NUMERIC }

mapper.select:
select 
	(case when count(0)>0 then 1 else 0 end) 
from wp_customer

	and id<>#{id, jdbcType=INTEGER}
	and cust_no=#{cust_no, jdbcType=VARCHAR}
	and mobile=#{mobile, jdbcType=VARCHAR}
	and password=#{password, jdbcType=VARCHAR}
	and nick_name=#{nick_name, jdbcType=VARCHAR}
	and wx_openid=#{wx_openid, jdbcType=VARCHAR}
	and head_url=#{head_url, jdbcType=VARCHAR}
	and email=#{email, jdbcType=VARCHAR}
	and h_number=#{h_number, jdbcType=VARCHAR}
	and register_ip=#{register_ip, jdbcType=VARCHAR}
	and last_login_ip=#{last_login_ip, jdbcType=VARCHAR}
	and org_mobile=#{org_mobile, jdbcType=VARCHAR}
	and org_wx_openid=#{org_wx_openid, jdbcType=VARCHAR}
	and share_code=#{share_code, jdbcType=VARCHAR}

select 
	count(0)
from wp_customer

	and id<>#{id, jdbcType=INTEGER}
	and cust_no=#{cust_no, jdbcType=VARCHAR}
	and mobile=#{mobile, jdbcType=VARCHAR}
	and password=#{password, jdbcType=VARCHAR}
	and nick_name=#{nick_name, jdbcType=VARCHAR}
	and wx_openid=#{wx_openid, jdbcType=VARCHAR}
	and email=#{email, jdbcType=VARCHAR}
	and h_number=#{h_number, jdbcType=VARCHAR}
	and register_ip=#{register_ip, jdbcType=VARCHAR}
	and last_login_ip=#{last_login_ip, jdbcType=VARCHAR}
	and org_mobile=#{org_mobile, jdbcType=VARCHAR}
	and org_wx_openid=#{org_wx_openid, jdbcType=VARCHAR}
	and share_code=#{share_code, jdbcType=VARCHAR}
	and agent_member_id=#{agent_member_id, jdbcType=VARCHAR}
